package dongmoo.arrayandlist;import java.util.Scanner;public class Problem03 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int k = sc.nextInt();        int n = sc.nextInt();        int[] A = new int[k];        int[] S = new int[k+1];        for (int i = 1; i < k + 1; i++) {            A[i - 1] = sc.nextInt();            S[i] = S[i - 1] + A[i - 1];        }        for (int i = 0; i < n; i++) {            int p = sc.nextInt();            int q = sc.nextInt();            System.out.println(S[q] - S[p - 1]);        }    }}