package dongmoo.arrayandlist;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.Arrays;import java.util.Scanner;import java.util.StringTokenizer;public class Problem04 {    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(br.readLine());        Scanner sc = new Scanner(System.in);        int n = Integer.parseInt(st.nextToken());        int k = Integer.parseInt(st.nextToken());        long[][] S = new long[n + 1][n + 1];        for (int i = 1; i < n + 1; i++) {            st = new StringTokenizer(br.readLine());            for (int j = 1; j < n + 1; j++) {                S[i][j] = S[i][j - 1] + Integer.parseInt(st.nextToken());            }        }        for (int i = 0; i < k; i++) {            st = new StringTokenizer(br.readLine());            int o = Integer.parseInt(st.nextToken());            int p = Integer.parseInt(st.nextToken());            int q = Integer.parseInt(st.nextToken());            int r = Integer.parseInt(st.nextToken());            int answer = 0;            while (r - p >= 0) {                answer += S[p][q] - S[p][o - 1];                p++;            }            System.out.println(answer);        }    }}